<?php//管理员相关class FinanceAction extends BaseAction{    public function finance_list($_action_access=0){        //访问控制变量        $GLOBALS['_action_access'] = $_action_access;        $export=_GET('_export');        if ($export) $_REQUEST=unserialize(base64_decode($_GET['_request']));        //声明变量        $GLOBALS['page_count'] = 10;$GLOBALS['totalcount'] = 0;        $keyid = $_POST['keyid'];        $type = $_POST['type'];        if(_POST('search-flag')){            unset($_POST['page']);            unset($_GET['page']);        }        $shop_id = $_SESSION['shop']['auth']['uid'];        //基本查询        $field_list = "mt.* ";        $sql_suffix = "from sys_income mt ";        $sql_suffix .= "where mt.shop_id=$shop_id and (mt.type=1 or mt.type=3 or mt.type=4) ";        $orderby_str = "mt.id desc";        //筛选数据        if($keyid){            $sql_suffix .= "and mt.id like '%$keyid%' ";        }        //时间段        $time_start=_REQUEST('time_start');        if ($time_start) $sql_suffix.=" and mt.regdate>='$time_start'";        $time_end=_REQUEST('time_end');        if ($time_end) $sql_suffix.=" and mt.regdate<='$time_end'";		if ($type) $sql_suffix .= " and mt.type=$type";        if ($export) $list_items=$this->get_list_bysql("select $field_list $sql_suffix order by $orderby_str");        else {            $list_items = $this -> admin_get_page($field_list,$sql_suffix,$orderby_str);            $list_item=$this->get_list_bysql("select $field_list $sql_suffix order by $orderby_str");        }        $arrival_fee = 0;        foreach($list_item as $k=>&$v){	        if($v['type'] == 1 || $v['type'] == 4){		        $v['totalfee'] = $v['arrival_fee'];		        		        $arrival_fee += $v['arrival_fee'];	        }        }        unset($v);        int_to_string($list_items,array(            'type'=>array('1'=>'销售收入','3'=>'提现支出','4'=>'扫码支付'),        ));        //UI部分        $breadcrumb_data = '首页,商家管理,财务管理';        $search_items = array(            array('name'=>'keyid','placeholder'=>'请输入财务记录ID','cls'=>'w250','_parser'=>'form_item/search/input'),            array('name'=>'time','label'=>'记录时间','_parser'=>'form_item/search/date'),          	array('name'=>'type','_parser'=>'form_item/search/select','data'=>array(''=>'记录类型','1'=>'销售收入','3'=>'提现支出','4'=>'扫码支付'),'style'=>'width:120px;'),        );        $buttons = array(        		['text'=>'申请提现','title'=>'申请提现','icon'=>'add2','full'=>0,'checked'=>0,'position'=>1,            	'target'=>'inner_frame','url'=>U(MODULE_NAME.'/cash_save'),'url_param'=>[]            ],            array('text'=>'备注','title'=>'备注','full'=>0,'checked'=>1,'position'=>2,                'target'=>'inner_frame','url'=>U('Finance/remarks_save'),'url_param'=>array('id'=>'2_id')            ),              array('position'=>1,'_after_parser'=>array('_parser'=>'button_item/position/blank','text'=>'导出Excel','title'=>'导出Excel','icon'=>'edit2',                'target'=>'blank','full'=>0,'url'=>U(MODULE_NAME.'/'.ACTION_NAME.'?_export=1&_request='.base64_encode(serialize($_REQUEST)))),            ),        );        $table_headers = array(            array('name'=>'id','title'=>'财务记录ID'),            array('name'=>'arrival_fee','title'=>'财务记录金额'),            array('name'=>'type_text','title'=>'财务记录类型'),            array('name'=>'regdate','title'=>'财务记录时间'),            array('name'=>'remark','title'=>'备注')        );        if ($export) {            $export_headers= array(                array('name'=>'id','title'=>'财务记录ID'),	            array('name'=>'totalfee','title'=>'财务记录金额'),	            array('name'=>'type_text','title'=>'财务记录类型'),	            array('name'=>'regdate','title'=>'财务记录时间'),	            array('name'=>'remark','title'=>'备注')            );            ext_export("财务列表",$export_headers,$list_items);        } else {	        $component_data = array(	            '_parser'=>'container/default',	            '_children'=>array(	                array('_parser'=>'breadcrumb/default','data'=>$breadcrumb_data),	                array('_parser'=>'container/content',	                    '_children'=>array(	                        array('_parser'=>'form/search','cls'=>'text-c','action'=>U(MODULE_NAME.'/'.ACTION_NAME),	                            '_children'=>$search_items	                        ),	                        array('_parser'=>'button/top_button','_children'=>$buttons),	                        array('_parser'=>'table/datatables/thin',	                            'head'=>&$table_headers,	                            'data'=>&$list_items,	                            'row_button'=>array('_parser'=>'button/row_dropdown','_children'=>$buttons)	                        ),	                        array('_parser'=>'pagination/laypage',	                            'total_count'=>$GLOBALS['totalcount'],	                            'page_count'=>$GLOBALS['page_count'],	                            'arrival_fee'=>$arrival_fee,	                        ),	                    )	                )	            )	        );    	}        //解析组件        _display($component_data);    }    public function cash_save(){	    $shop_id = $_SESSION['shop']['auth']['uid'];        if(IS_POST){            $id = _POST('id');            $GLOBALS['cur_operate'] = $id ? 2 : 1;//目前的操作类型，2^0:新增;2^1:编辑            //获取字段            $feeaccount = _POST('feeaccount');            $score = _POST('score');                 if($feeaccount < $score){				sys_out_fail("余额不足");	        }                 $type = _POST('type');            if($type == 1){	            $alipay_account = _POST('alipay_account');	            $sqlstr_array[] = "insert into sys_allcash set shop_id='$shop_id',score='$score',keytype=2,regdate='".sys_get_time()."',type=1,flag=1,alipay_account='$alipay_account'";            }else{	            $bank_id = _POST('bank_id');	            $bank = $this->get_one_bysql("select name from sys_bank where id=$bank_id");	            $bankcard = _POST('bankcard');	            $bankcard_name = _POST('bankcard_name');	            $sqlstr_array[] = "insert into sys_allcash set shop_id='$shop_id',score=$score,keytype=2,regdate='".sys_get_time()."',type=2,flag=1,bank='$bank',bankcard_name='$bankcard_name',bankcard='$bankcard'";            }            $sqlstr_array[] = "update sys_shop set feeaccount=feeaccount-$score where id='$shop_id'";             $result = $this->do_transaction($sqlstr_array);                       sys_out_result($result);        }        else{            $id = _REQUEST('id');            $GLOBALS['cur_operate'] = $id ? 2 : 1;//目前的操作类型，2^0:新增;2^1:编辑            $form_items = array(                array('name'=>'id','_parser'=>'form_item/form/hidden'),                ['name'=>'feeaccount','label'=>'可提现金额','required'=>0,                	'_parser'=>'form_item/form/input','type'=>'text','readonly'=>1,                	'_validation'=>[],                ],                ['name'=>'score','label'=>'提现金额','placeholder'=>'','required'=>1,                	'_parser'=>'form_item/form/input','type'=>'text',                	'_validation'=>array(                        'isNumber'=>array(true,"必须是数字"),                        'min'=>[1, '最小值1'],                    ),                ],                array('name'=>'type','label'=>'提现方式',					'_parser'=>'form_item/collect/radio',					'data'=>array('1'=>'支付宝提现','2'=>'银行卡提现'),'default'=>'1'				),				['name'=>'alipay_account','label'=>'支付宝账号','placeholder'=>'','required'=>0,                	'_parser'=>'form_item/form/input','type'=>'text',                	'_validation'=>[],                ],                ['name'=>'bank','label'=>'提现银行',                    '_parser'=>'form_item/form/cascade_select_bind','type'=>'text','placeholder'=>'','required'=>0,                    'related'=>[                        ['name'=>'bank_id','label'=>'选择银行'],                    ],                    'init_url'=>U(MODULE_NAME.'/bank_list'),                ],				['name'=>'bankcard','label'=>'卡号','placeholder'=>'','required'=>0,                	'_parser'=>'form_item/form/input','type'=>'text',                	'_validation'=>[],                ],				['name'=>'bankcard_name','label'=>'户名','placeholder'=>'','required'=>0,                	'_parser'=>'form_item/form/input','type'=>'text',                	'_validation'=>[],                ],            );            form_validation_create($form_items,$rules,$messages);//获取验证规则            if($shop_id) {                //取值                $field_list = "mt.*";                $sql_suffix = "from sys_shop mt ";                $sql_suffix .= "where mt.id=$shop_id ";                $temp_array = $this->get_list_bysql("select $field_list $sql_suffix");                form_item_add_value($form_items,$temp_array[0]);//赋值            }            $component_data = array('_parser'=>'form/default',                'action' => U(MODULE_NAME.'/'.ACTION_NAME),'_children'=>$form_items,                'rules' => $rules,'messages' => $messages,            );            _display($component_data);        }    }    public function bank_list(){	    $sqlstr = "select id,name from sys_bank";        $temp_array = $this -> get_list_bysql($sqlstr);        sys_out_success(0,$temp_array);    }    public function remarks_save(){        if(IS_POST){            $id = _POST('id');            $GLOBALS['cur_operate'] = $id ? 2 : 1;//目前的操作类型，2^0:新增;2^1:编辑            //获取字段            $save_fields = array('remark');            $post_keys = array_keys($_POST);            $post_fields = array_intersect($save_fields,$post_keys);//取公共            $fields_str = fields2SqlStrByPost($post_fields);            if($id){//修改                $sqlstr = "update sys_income set $fields_str where id=$id";                $result = $this -> do_execute($sqlstr);            }            sys_out_result($result);        }        else{            $id = _REQUEST('id');            $GLOBALS['cur_operate'] = $id ? 2 : 1;//目前的操作类型，2^0:新增;2^1:编辑            $form_items = array(                array('name'=>'id','_parser'=>'form_item/form/hidden'),                array('_parser'=>'tab/default','_children'=>array(                    array('label'=>'基本信息','_parser'=>'container/default','_children'=>array(                        array('name'=>'remark','label'=>'备注','placeholder'=>'最长140个字符',                            '_parser'=>'form_item/form/textarea','required'=>0,                            '_validation'=>array(                                'maxlength'=>array(140,"最长140个字符")                            ),                        ),                    ))                )),            );            form_validation_create($form_items,$rules,$messages);//获取验证规则            if($id) {                //取值                $field_list = "mt.*";                $sql_suffix = "from sys_income mt ";                $sql_suffix .= "where mt.id=$id ";                $temp_array = $this->get_list_bysql("select $field_list $sql_suffix");                form_item_add_value($form_items,$temp_array[0]);//赋值            }            $component_data = array('_parser'=>'form/default',                'action' => U(MODULE_NAME.'/'.ACTION_NAME),'_children'=>$form_items,                'rules' => $rules,'messages' => $messages,            );            _display($component_data);        }    }}